import React, { useState } from 'react';
import Header from '../Header/Header';
import { Link } from 'react-router-dom';
import { ReactComponent as ArrowBack } from '../../assets/images/arrowback.svg';
import Last_message from '../Message/Last_message';
import Open_message from './Open_message';
import WSS from '../WSS';


const Message: React.FC = () => {
  const [selectedContact, setSelectedContact] = useState<string | null>(null);

  const handleContactClick = (contactName:string) =>{
    setSelectedContact(contactName);
  };

const renderChatContent = () =>{
  if (selectedContact === "Обама"){
    return <div>
      <Open_message/>
    </div>
  }else{
    return <div>
      Choice cchat
    </div>
  }
};


    return (
