import React, { useState } from 'react';
import './RatingMember.css'; // Подключаем файл стилей

interface RatingProps {
  number: string;
  userName: string;
  Group: string;
  Rate: string;
  Nac: string;
}

const RatingMember: React.FC<RatingProps> = (props) => {
  const [backgroundColor, setBackgroundColor] = useState<string>(''); // Состояние для цвета фона

  // Определяем цвет фона в зависимости от номера
  useState(() => {
    setBackgroundColor(parseInt(props.number) % 2 === 0 ? 'lightblue' : 'lightgreen');
  }, []);

  return (
    <div className='RatingMember' style={{ backgroundColor }}>
      <div id='number'>{props.number}</div>
      <div id='userName'>{props.userName}</div>
      <div id='group'>{props.Group}</div>
      <div id='rate'>{props.Rate}</div>
      <div id='nac'>{props.Nac}</div>
    </div>
  );
};

export default RatingMember;
