import { AxiosResponse } from "axios";
import $api from "../http";
import { IRating } from "../models/IRating";

export default class RatingService {
    static fetchRatings(specialBlock: string, semester: number): Promise<AxiosResponse<IRating[]>> {
        return $api.get<IRating[]>('/ratings', {
            params: { specialBlock, semester }
        });
    }

    static fetchRatingById(id: string): Promise<AxiosResponse<IRating>> {
        return $api.get<IRating>(`/ratings/${id}`);
    }
}
