import axios from 'axios';
import { API_URL } from '../http';
import { IGrade } from '../models/IGrade';

class GradeService {
    static async getGrades(profileId: string) {
        return axios.get<IGrade[]>(`${API_URL}/profiles/${profileId}/grades`);
    }

    static async getGrade(gradeId: string) {
        return axios.get<IGrade>(`${API_URL}/grades/${gradeId}`);
    }

    static async addGrade(profileId: string, gradeData: Partial<IGrade>) {
        return axios.post<IGrade>(`${API_URL}/profiles/${profileId}/grades`, gradeData);
    }

    static async updateGrade(gradeId: string, gradeData: Partial<IGrade>) {
        return axios.put<IGrade>(`${API_URL}/grades/${gradeId}`, gradeData);
    }

    static async deleteGrade(gradeId: string) {
        return axios.delete(`${API_URL}/grades/${gradeId}`);
    }
}

export default GradeService;
