import React, { useState } from 'react';
import Header from '../Header/Header';
import { Link } from 'react-router-dom';
import { ReactComponent as ArrowBack } from '../../assets/images/arrowback.svg';
import Last_message from '../Message/Last_message';
import Open_message from './Open_message';
import WSS from '../WSS';


const Message: React.FC = () => {
  const [selecredContact, setSelectedContact] = useState<string | null>(null);

  const handleContactClick = (contactName:string) =>{
    setSelectedContact(contactName);
  };


    return (
      <div>
      <Header/>
      <div className='hello'>
        <div id='hello'>Мессенджер:</div>
          <Link to='/'>
            <div className='back'>
              <ArrowBack />
                Назад
            </div>
          </Link>
      </div>

      <div className='chat'>
          <div className='last_message'>
            <div className='search_message'>
              <input className='message_input' placeholder='Пошук'></input>
              </div>
            <Last_message
            userName='Барак Обама'
            time='13:20'
            message='Я відправлю сюди код від ядерки, добре?'
            onClick={() => handleContactClick("Обама")}
            />
          </div>

          <div className='chat_now'>

            
            <div id='chat_now'>Оберіть, кому хотіли б написати</div>

           
          </div>
          <Open_message/>
      </div>


      </div>
    );
  };

export default Message;
