import { AxiosResponse } from "axios";
import $api from "../http";
import { IRating } from "../models/IRating";

export default class RatingService {
    static fetchRatings(groupIds: string[]): Promise<AxiosResponse<IRating[]>> {
        return $api.get<IRating[]>('/ratings', { params: { groups: groupIds } });
    }

    static createRating(groupIds: string[]): Promise<AxiosResponse<IRating>> {
        return $api.post<IRating>('/ratings', { groups: groupIds });
    }
}
